import './menu.css';

const url = 'https://raw.githubuserprice.com/aleisabl/Burger-Queen/take-order/burger-queen/src/menu/burger-queen.json';

class ComponentData extends Component {


  componentDidMount() {
    axios
      .get(url)
      .then(response =>
        response.Desayunos.map(desayunos => ({
          name: `${desayunos} `
        }))
      )
      .then(users => {
        this.setState({
          users,
          isLoading: false
        });
      })
      .catch(error => this.setState({ error, isLoading: false }));
  }


