import React, {Component} from 'react';
import axios from 'axios';

const url = 'https://raw.githubusercontent.com/aleisabl/Burger-Queen/take-order/burger-queen/src/menu/burger-queen.json'

class ComponentData extends Component {
  state = {
    menu: [],
    isLoading: true,
    errors: null
  };

  render() {
    return (
      <React.Fragment>
      </React.Fragment>
    );
  }


  componentDidMount() {
    axios
      .get(url)
      .then(response =>

     /*    console.log(response.data.Desayunos[0].name) */
        response.data.map(menu => ({
          name: `${menu.name}`,
          image: `${user.picture.thumbnail}`
        })),
      )
      .then(menu => {
        this.setState({
          menu,
          isLoading: false
        });
      })
      .catch(error => this.setState({ error, isLoading: false }));
  }


}



export default ComponentData;
