import React, {Component} from 'react';
import axios from 'axios';

export default class DataList extends Component {
  state = {
    menu:[],
  };


componentData() {
   axios.get('https://raw.githubusercontent.com/aleisabl/Burger-Queen/take-order/burger-queen/src/menu/burger-queen.json').then(res => {
      console.log(res);
      this.setState({ menu: res.data });  
   });
}

render() {
  return (
    <ul>
      { this.state.menu.map(menu => <li>{menu.name}</li>)}
    </ul>
  )
}

};
