import React, {Component} from 'react';
import axios from 'axios';
import './menu.css';

const url = 'https://raw.githubuserprice.com/aleisabl/Burger-Queen/take-order/burger-queen/src/menu/burger-queen.json';

class ComponentData extends Component {

  state = {
    menu: [],
    isLoading: true,
    errors: null

  }

  componentDidMount() {
    axios
      .get(url)
      .then(response =>
        response.Desayunos.map(desayunos => ({
          name: `${desayunos} `
        }))
      )
      .catch(error => this.setState({ error, isLoading: false }));
  }

  render() {
    const { isLoading, menu } = this.state;
    return (
      <React.Fragment>
        <h2>Random User</h2>
        <div>
          {!isLoading ? (
            menu.map(items => {
              return (
                  <p>{items}</p>
              );
            })
          ) : (
            <p>Loading...</p>
          )}
        </div>
      </React.Fragment>
    );
  }


