import React, {Component} from 'react';
import axios from 'axios';
import './menu.css';

const url = 'https://raw.githubuserprice.com/aleisabl/Burger-Queen/take-order/burger-queen/src/menu/burger-queen.json';
const api = axios.create({
  baseURL: "https://raw.githubuserprice.com/aleisabl/Burger-Queen/take-order/burger-queen/src/menu/burger-queen.json"
});

class ComponentData extends Component {
  // State will apply to the Desayunos object which is set to loading by default
  state = {
    Desayunos: [],
    isLoading: true,
    errors: null
  };
  // Now we're going to make a request for data using axios
  getDesayunos() {
    axios
      // This is where the data is hosted
      .get("https://s3-us-west-2.amazonaws.com/s.cdpn.io/3/Desayunos.json")
      // Once we get a response and store data, let's change the loading state
      .then(response => {
        this.setState({
          Desayunos: response.data.Desayunos,
          isLoading: false
        });
      })
      // If we catch any errors connecting, let's update accordingly
      .catch(error => this.setState({ error, isLoading: false }));
  }
  // Let's our app know we're ready to render the data
  componentDidMount() {
    this.getDesayunos();
  }
  // Putting that data to use
  render() {
    const { isLoading, Desayunos } = this.state;
    return (
      <React.Fragment>
        <h2>Random desayuno</h2>
        <div>
          {!isLoading ? (
            Desayunos.map(desayuno => {
              const { id, name, price } = desayuno;
              return (
                <div key={id}>
                  <h2>{name}</h2>
                  <p>{price}</p>
                  <hr />
                </div>
              );
            })
          ) : (
            <p>Loading...</p>
          )}
        </div>
      </React.Fragment>
    );
  }
}

export default ComponentData
