import React, { Component } from 'react';

const API = 'https://raw.githubusercontent.com/aleisabl/SCL008-BurgerQueen/take-order/burger-queen/burger-queen.json';
const DEFAULT_QUERY = 'redux';

class Data extends Component {
  constructor(props) {
    super(props);

    this.state = {
      hits: [],
    };
  }

  componentDidMount() {
    fetch(API + DEFAULT_QUERY)
      .then(response => response.json())
      .then(data => this.setState({ hits: data.hits }));
  }

}

export default Data;
