import React, {Component} from 'react';
import './menu.css';
import ComponentBreakfast from './breakfast.js'

class ComponentButtons extends Component {

        add = () => {
            this.setState({ addContainer: true }) 
          }

    render() {
      return (

        <React.Fragment>
        <div className="rectangle">
        <button type="button" onClick={() => this.add()} className="btn btn-outline-info">Desayunos</button> 
        { this.state.render &&
        <ComponentBreakfast/>
     }  
        <button type="button" onClick={() => this.add()} className="btn btn-outline-info2">Almuerzos</button>

        </div>

        <div className="rectangle2">
        </div>      

</React.Fragment>
      );
    }
  }






export default ComponentButtons;
