import React, {Component} from 'react';
import axios from 'axios';
import './menu.css';

const url = 'https://raw.githubusercontent.com/aleisabl/Burger-Queen/take-order/burger-queen/src/menu/burger-queen.json'

class ComponentData extends Component {
  state = {
    menu: [],
    isLoading: true,
    errors: null
  };

  componentDidMount() {
    axios
      .get(url)
      .then(response =>

       console.log(response.data.Desayunos[0].name)


      )
      .then(menu => {
        this.setState({
          menu,
          isLoading: false
        });
      })
      .catch(error => this.setState({ error, isLoading: false }));
  }


  render() {
    return (
      <React.Fragment>
        <table className="table table-bordered">
  <thead>
    <tr>
      <th scope="col"> {response.data.Desayunos[0].name} </th>
      <th scope="col">Precio</th>
      <th scope="col">Cantidad</th>
    </tr>
  </thead>
  <tbody>
    <tr>
      <th scope="row">1</th>
      <td>Mark</td>
      <td>Otto</td>
    </tr>
    <tr>
      <th scope="row">2</th>
      <td>Jacob</td>
      <td>Thornton</td>
    </tr>
    <tr>
      <th scope="row">3</th>
      <td>Larry the Bird</td>
      <td>@twitter</td>
    </tr>
  </tbody>
</table> 
      </React.Fragment>
    );
  }


}



export default ComponentData;
