import React, {Component} from 'react';
import './menu.css';

class ComponentNameForm extends Component {
    constructor(props) {
      super(props);
      this.state = {value: ''};

      this.handleChange = this.handleChange.bind(this);
      this.handleSubmit = this.handleSubmit.bind(this);
    }

    handleChange(event) {
      this.setState({value: event.target.value});
    }

    handleSubmit(event) {
      alert('Nombre del cliente: ' + this.state.value);
      event.preventDefault();
    }

    render() {
      return (
        <form className="nameForm" onSubmit={this.handleSubmit}>
          <label>
            Nombre: 
            <input type="text" value={this.state.value} onChange={this.handleChange} />
          </label>
          <input className="nameSubmit" type="submit" value="Ingresar" />
        </form>
      );
    }
  }

  export default ComponentNameForm;
