import React, {Component} from 'react';
import './menu.css';
import ComponentData from './data.js'


class ComponentButtons extends Component {


      constructor(props) {
        super(props);
        this.state = {isToggleOn: true};

        // This binding is necessary to make `this` work in the callback
        this.handleClick = this.handleClick.bind(this);
      }

      handleClick() {
        this.setState(prevState => ({
          isToggleOn: !prevState.isToggleOn
        }));
      }

    render() {
      return (

        <React.Fragment>

        <div className="rectangle">
        <button type="button" onClick={this.handleClick} className="btn btn-outline-info">Desayunos</button> 
        {this.state.isToggleOn ? <ComponentData/> : null}
        <button type="button" onClick={this.handleClick} className="btn btn-outline-info2">Almuerzos</button>
        </div>

        <div className="rectangle2">
        </div>      

        </React.Fragment>
      );
    }
  }






export default ComponentButtons;
