class ComponentData extends Component {
  state = {
    posts: [],
    isLoading: true,
    errors: null
  };

async getPosts() {
  const response = await api.get();
  try {
    this.setState({
      posts: response.data.posts,
      isLoading: false
    });
  } catch (error) {
    this.setState({ error, isLoading: false });
  }
}

render() {
  const { isLoading, posts } = this.state;
  return (
    <React.Fragment>
      <h2>Men√∫ de Desayunos</h2>
      <div>
        {!isLoading ? (
          posts.map(post => {
            const { _id, title, content } = post;
            return (
              <div key={_id}>
                <h2>{title}</h2>
                <p>{content}</p>
                <hr />
              </div>
            );
          })
        ) : (
          <p>Loading...</p>
        )}
      </div>
    </React.Fragment>
  )
}
}

export default ComponentData;
